import { Box } from '@material-ui/core';
import axios from 'axios';
import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import './App.css';
import { FilmCard } from './components/filmCard';

function App() {

  const [data, setData] = useState<any[]>([]); 
  const [dataLiked, setDataLiked] = useState<any[]>([]);

  useEffect(() => {
    const getMovieRequest = async () => {
      const result = await axios(
        'https://api.themoviedb.org/3/discover/tv?api_key=92b418e837b833be308bbfb1fb2aca1e&language=fr-FR&sort_by=popularity.desc&page=1&timezone=America/New_York&include_null_first_air_dates=false',
      );
      setData(result.data.results);
    }
    getMovieRequest();
  },[]);



  const [currentId, setCurrentId] = useState(0)
  const onClickLike = (id: number) => {
    const film = findItemInArrayOfObject(data, id)
    if(findItemInArrayOfObject(dataLiked, id)){
      alert('déjà ajouté !')
      const newList = dataLiked.filter((v) => v !== findItemInArrayOfObject(dataLiked, id))
      setDataLiked([...newList])
    } else {
      setDataLiked([...dataLiked, film])
      const newList = data.filter((v) => v !== findItemInArrayOfObject(data, id))
      setData([...newList])
    }
  }

  const findItemInArrayOfObject = (
    arrayOfObject: Array<Record<string, any>>,
    id: number,
    keyOfId = 'id',
  ): Record<string, any> | undefined => {
    const obj = arrayOfObject.find((x) => x[keyOfId] === id) 
    return obj
  }

  const onDeleteFilmOnData = (id: any): void => {
    const upDateList = data.filter((v) => v.id !== id)
    setData([...upDateList])
  }

  // const onDeleteFilmOnDataLiked = (id: any): void => {
  //   // const upDateList = dataLiked.filter((v) => v.id !== id)
  //   // setDataLiked([...upDateList])
  //   console.log('updatedlist', dataLiked.filter((v) => v !== id))
  // }

  const filteredArray = (
    arrayOfObject: Array<Record<string, any>>,
  ): void => {
    arrayOfObject.filter(function(ele , pos){
      return arrayOfObject.indexOf(ele) == pos;
  })}

  return (
    <Box className="App">
      <header className="App-header">
        <h1>Cine React</h1>
      </header>
      <section className="Section">
        <h1>Films et séries du moment aux US</h1>
        <Box display="flex" flexDirection="row" className="image-container">
          {data.map(item => (
            <FilmCard 
              key={item.id}
              id={item.id}
              name={item.name} 
              textOverview={item.overview} 
              poster_path={item.poster_path} 
              backdrop_path={item.backdrop_path}
              date={item.first_air_date}
              vote_average={item.vote_average}
              totalLikes={item.popularity}
              vote_count={item.vote_count}
              onClickLike={onClickLike}
            />              
          ))}
        </Box>
      </section>
      <section className="Section">
        <h1>Films et séries en favoris</h1>
        <Box display="flex" flexDirection="row" className="image-container">
          {dataLiked.map(item => (
            <FilmCard 
              key={item.id}
              id={item.id}
              name={item.name} 
              textOverview={item.overview} 
              poster_path={item.poster_path} 
              backdrop_path={item.backdrop_path}
              date={item.first_air_date}
              vote_average={item.vote_average}
              totalLikes={item.popularity}
              vote_count={item.vote_count}
              onClickLike={onClickLike}
            />              
          ))}
        </Box>
      </section>
    </Box>
  );
}

export default App;
